export default function Productos({ filtros }) {

  const [mensaje, setMensaje] = useState("");
  const { agregarAlCarrito } = useContext(CarritoContext);
  const [productos, setProductos] = useState([]);
  const [productosFiltrados, setProductosFiltrados] = useState([]);

  useEffect(() => {
    obtenerProductos().then(data => {
      setProductos(data);
      setProductosFiltrados(data);
    });
  }, []);

  useEffect(() => {
    let filtrados = productos;

    if (filtros.categoria) {
      filtrados = filtrados.filter(p => p.categoria === filtros.categoria);
    }

    if (filtros.estilo) {
      filtrados = filtrados.filter(p => p.estilo === filtros.estilo);
    }

    if (filtros.talla) {
      filtrados = filtrados.filter(p => p.talla === filtros.talla);
    }

    filtrados = filtrados.filter(p => 
      p.precio >= filtros.minPrice && p.precio <= filtros.maxPrice
    );

    setProductosFiltrados(filtrados);
  }, [filtros, productos]);

  return (
    <article className="art-newArrivals">

      <div className="box-tarjetas-newArrivals">

        {productosFiltrados.map(p => (
          <div key={p.id_producto} className="tarjeta-newArrivals">

            <div className="box-img-newArrivals">
              <Link to={`/producto/${p.id_producto}`}>
                <img className="img-newArrivals" src={p.imagen} alt={p.nombre}/>
              </Link>
            </div>

            <div className="box-info-newArrivals">
              <h3>{p.nombre}</h3>
              <p>{p.descripcion}</p>
              <span>Q{p.precio}</span>

              <button onClick={() => { 
                agregarAlCarrito(p);
                setMensaje("Producto agregado al carrito");
                setTimeout(()=> setMensaje(""), 2000);
              }}>
                Agregar al carrito
              </button>
            </div>
          </div>
        ))}

        {mensaje && <div className="alerta">{mensaje}</div>}

      </div>
    </article>
  );
}
