import { useContext, useState } from 'react';
import { CarritoContext } from '../context/CarritoContext';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faPlus, faMinus } from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';
import { enviarPedido } from '../api/carrito_api';

export default function Carrito() {

    // üîπ Mensaje temporal
    const [mensaje, setMensaje] = useState("");

    // üîπ Datos del carrito
    const { carrito, eliminarDelCarrito, reducirCantidad, aumentarCantidad, vaciarCarrito } = useContext(CarritoContext);

    // üßÆ Calcular subtotal
    const subtotal = carrito.reduce((acc, p) => acc + p.precio * p.cantidad, 0);

    // üßÆ Calcular descuento (20%)
    const descuento = subtotal * 0.20;

    // üßÆ Env√≠o
    const envio = subtotal >= 1000 ? 0 : 30;

    // üßÆ Total final
    const total = subtotal - descuento + envio;

    // üîπ FUNCION PARA PROCESAR PAGO + ENVIAR AL PHP
    async function procesarPago() {
        
        if (carrito.length === 0) {
            setMensaje("Tu carrito est√° vac√≠o.");
            setTimeout(() => setMensaje(""), 3000);
            return;
        }

        // üîπ Construcci√≥n del JSON EXACTO para el PHP
        const datos = {
            id_pedido: Date.now(), // Puedes cambiar seg√∫n tu l√≥gica
            id_usuario: 1, // Cambia seg√∫n tu sistema de login
            carrito: carrito.map(p => ({
                id_producto: p.id_producto,
                precio_unitario: p.precio,
                cantidad: p.cantidad,
                subtotal: p.precio * p.cantidad
            })),
            pago: {
                nombre_tarjeta: "Cliente",
                numero_tarjeta: "0000111122223333",
                fecha_expiracion: "12/30",
                cvv: "123"
            }
        };

        try {
            const respuesta = await enviarPedido(datos);

            if (respuesta.mensaje) {
                setMensaje("¬°Compra realizada con √©xito! üéâ");
                vaciarCarrito();

                setTimeout(() => setMensaje(""), 3000);
            } else {
                setMensaje("Ocurri√≥ un error al procesar tu compra.");
                setTimeout(() => setMensaje(""), 3000);
            }

        } catch (error) {
            console.error("Error enviando pedido:", error);
            setMensaje("Error al conectar con el servidor.");
            setTimeout(() => setMensaje(""), 3000);
        }
    }

    return (
        <article className='cart-page'>

            {/* üîπ Mostrar mensaje temporal */}
            {mensaje && (
                <div className="alerta-compra">{mensaje}</div>
            )}

            <div className="breadcrumb">Inicio / Carrito</div>
            <h1>Mi Carrito de Compras</h1>

            <section className="cart-content">

                {/* üõí LISTA DE PRODUCTOS */}
                <div className='contenedor-pedidos'>
                    {carrito.length === 0 && <p>No hay productos en tu carrito.</p>}

                    {carrito.map(p => (
                        <div key={p.id_producto} className="tarjeta-carrito">

                            <div className="box-img-carrito">
                                <Link className="link-newArrivals" to={`/producto/${p.id_producto}`}>
                                    <img className="img-carrito" src={p.imagen} alt={p.nombre} />
                                </Link>
                            </div>

                            <div className="box-info-carrito">
                                <Link className="link-newArrivals" to={`/producto/${p.id_producto}`}>
                                    <h3>{p.nombre}</h3>
                                    <p>{p.descripcion}</p>
                                    <span>Q{p.precio}</span>
                                </Link>
                            </div>

                            <div className='box-controles'>
                                <button className="btn-eliminar" onClick={() => eliminarDelCarrito(p.id_producto)}>
                                    <FontAwesomeIcon icon={faTrash} />
                                </button>

                                <div className="cantidad-controles">
                                    <button className='control' onClick={() => reducirCantidad(p.id_producto)}>
                                        <FontAwesomeIcon icon={faMinus} />
                                    </button>

                                    <span>{p.cantidad}</span>

                                    <button className='control' onClick={() => aumentarCantidad(p.id_producto)}>
                                        <FontAwesomeIcon icon={faPlus} />
                                    </button>
                                </div>
                            </div>

                        </div>
                    ))}
                </div>

                {/* üßæ RESUMEN */}
                <aside className="cart-summary">
                    <h2>Resumen del Pedido</h2>

                    <div className="summary-line">
                        <span>Subtotal</span>
                        <span>Q{subtotal.toFixed(2)}</span>
                    </div>

                    <div className="summary-line">
                        <span>Descuento (-20%)</span>
                        <span>-Q{descuento.toFixed(2)}</span>
                    </div>

                    <div className="summary-line">
                        <span>Costo de Env√≠o</span>
                        <span>Q{envio.toFixed(2)}</span>
                    </div>

                    <div className="summary-line total-line">
                        <span>Total</span>
                        <span className="total-amount">Q{total.toFixed(2)}</span>
                    </div>

                    <button className="checkout-btn" onClick={procesarPago}>
                        Proceder al Pago
                    </button>
                </aside>
            </section>

            <div className="cart-info">
                <p>üõçÔ∏è Devoluci√≥n gratuita en 30 d√≠as</p>
                <p>üöö Env√≠o gratis en pedidos mayores a Q1000</p>
            </div>

        </article>
    );
}
