import { useState, useContext } from "react";
import Productos from "./Productos";
import { BusquedaContext } from "../context/BusquedaContext";

export default function Categoria() {

  const { termino } = useContext(BusquedaContext); // ðŸŸ¢ AGREGADO

  const [filtros, setFiltros] = useState({
    categoria: "",
    tipo_prenda: "",
    estilo: "",
    talla: "",
    color: "",
    minPrice: 0,
    maxPrice: 9999,
    busqueda: "" // ðŸŸ¢ AGREGADO
  });

  const actualizarFiltro = (campo, valor) => {
    setFiltros(prev => ({
      ...prev,
      [campo]: valor
    }));
  };

  // ðŸŸ¢ Cada vez que cambia el tÃ©rmino del Header, lo enviamos a productos
  useEffect(() => {
    setFiltros(prev => ({
      ...prev,
      busqueda: termino.toLowerCase()
    }));
  }, [termino]);

  return (
    <article className="category-page">

      <section className="category-content">
        
        {/* FILTROS */}
        <aside className="filters">
          {/* ... tus filtros como ya los tienes ... */}
        </aside>

        {/* LISTA DE PRODUCTOS */}
        <section className="product-listing">
          <Productos filtros={filtros}/>
        </section>

      </section>

    </article>
  );
}
