import { productos } from "../assets/imagenes/apis/data"
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";


export default function NewArrivals() {
  return (
    <article className="art-newArrivals">
      <h2 className="">Ofertas en Tablets</h2>

      <Swiper
        modules={[Navigation, Pagination]}
        spaceBetween={0}
        slidesPerView={4}
        navigation
        pagination={{ clickable: true }}
        breakpoints={{
          320: { slidesPerView: 1 },
          640: { slidesPerView: 2 },
          1024: { slidesPerView: 4 },
        }}
      >
        {productos.map((producto) => (
          <SwiperSlide key={producto.id}>
            <div className="tarjeta-newArrivals">

              <div className="box-img-slider">
                <img src={producto.image} alt={producto.name} className="img-slider"/>
              </div>

              <div className="box-info-slider">
                <h3 className="">{producto.name}</h3>
                <p className="">{producto.price}</p>
                {producto.discount && (
                  <span className="">{producto.discount}</span>
                )}
              </div>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </article>
  );
};
