import '../estilos/carrito-productos.css';
import { useContext, useState } from 'react';
import { CarritoContext } from '../context/CarritoContext';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faPlus, faMinus } from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';
import { enviarPedido } from '../api/carrito_api';

export default function Carrito() {

    // ðŸ”¹ Mensaje temporal
    const [mensaje, setMensaje] = useState("");

    // ðŸ”¹ Pago (aÃºn bÃ¡sico)
    const [tarjeta, setTarjeta] = useState({
        nombre_tarjeta: "",
        numero_tarjeta: "",
        fecha_expiracion: "",
        cvv: ""
    });

    const { carrito, eliminarDelCarrito, reducirCantidad, aumentarCantidad, vaciarCarrito } = useContext(CarritoContext);

    // ðŸ§® Totales
    const subtotal = carrito.reduce((acc, p) => acc + p.precio * p.cantidad, 0);
    const descuento = subtotal * 0.20;
    const envio = subtotal >= 1000 ? 0 : 30;
    const total = subtotal - descuento + envio;

    // ðŸ”¥ Enviar pedido al backend PHP
    async function procesarPago() {
        if (carrito.length === 0) {
            setMensaje("Tu carrito estÃ¡ vacÃ­o");
            return;
        }

        // ValidaciÃ³n bÃ¡sica del formulario de pago
        if (
            tarjeta.nombre_tarjeta.trim() === "" ||
            tarjeta.numero_tarjeta.trim() === "" ||
            tarjeta.fecha_expiracion.trim() === "" ||
            tarjeta.cvv.trim() === ""
        ) {
            setMensaje("Completa los datos de la tarjeta");
            return;
        }

        // Datos completos para el backend
        const datos = {
            id_pedido: Date.now(), // generamos un id temporal
            id_usuario: 1, // CAMBIAR cuando tengas login
            carrito: carrito.map(p => ({
                id_producto: p.id_producto,
                cantidad: p.cantidad,
                precio_unitario: p.precio,
                subtotal: (p.precio * p.cantidad)
            })),
            pago: tarjeta
        };

        try {
            const respuesta = await enviarPedido(datos);

            if (respuesta.error) {
                setMensaje("Error: " + respuesta.error);
                return;
            }

            // âœ” Ã‰XITO
            setMensaje("Â¡Compra realizada con Ã©xito! ðŸŽ‰");

            vaciarCarrito();

            setTimeout(() => {
                setMensaje("");
            }, 3000);

        } catch (error) {
            console.log(error);
            setMensaje("Error al conectar con el servidor.");
        }
    }

    return (
        <article className='cart-page'>

            {/* ðŸ”¹ Mostrar mensaje temporal */}
            {mensaje && (
                <div className="alerta-compra">
                    {mensaje}
                </div>
            )}

            <div className="breadcrumb">Inicio / Carrito</div>
            <h1>Mi Carrito de Compras</h1>

            <section className="cart-content">

                <div className='contenedor-pedidos'>
                    {carrito.length === 0 && (
                        <p>No hay productos en tu carrito.</p>
                    )}

                    {carrito.map(p => (
                        <div key={p.id_producto} className="tarjeta-carrito">

                            <div className="box-img-carrito">
                                <Link to={`/producto/${p.id_producto}`}>
                                    <img className="img-carrito" src={p.imagen} alt={p.nombre} />
                                </Link>
                            </div>

                            <div className="box-info-carrito">
                                <Link to={`/producto/${p.id_producto}`}>
                                    <h3>{p.nombre}</h3>
                                    <p>{p.descripcion}</p>
                                    <span>Q{p.precio}</span>
                                </Link>
                            </div>

                            <div className='box-controles'>
                                <button className="btn-eliminar" onClick={() => eliminarDelCarrito(p.id_producto)}>
                                    <FontAwesomeIcon icon={faTrash} />
                                </button>

                                <div className="cantidad-controles">
                                    <button className='control' onClick={() => reducirCantidad(p.id_producto)}>
                                        <FontAwesomeIcon icon={faMinus} />
                                    </button>

                                    <span>{p.cantidad}</span>

                                    <button className='control' onClick={() => aumentarCantidad(p.id_producto)}>
                                        <FontAwesomeIcon icon={faPlus} />
                                    </button>
                                </div>
                            </div>

                        </div>
                    ))}

                </div>

                {/* ðŸ§¾ RESUMEN */}
                <aside className="cart-summary">
                    <h2>Resumen del Pedido</h2>

                    <div className="summary-line">
                        <span>Subtotal</span>
                        <span>Q{subtotal.toFixed(2)}</span>
                    </div>

                    <div className="summary-line">
                        <span>Descuento (-20%)</span>
                        <span>-Q{descuento.toFixed(2)}</span>
                    </div>

                    <div className="summary-line">
                        <span>Costo de EnvÃ­o</span>
                        <span>Q{envio.toFixed(2)}</span>
                    </div>

                    <div className="summary-line total-line">
                        <span>Total</span>
                        <span className="total-amount">Q{total.toFixed(2)}</span>
                    </div>

                    {/* ðŸ”¹ Formulario bÃ¡sico de pago */}
                    <h3>InformaciÃ³n de Pago</h3>

                    <input 
                        type="text"
                        placeholder="Nombre del titular"
                        onChange={e => setTarjeta({...tarjeta, nombre_tarjeta: e.target.value})}
                    />

                    <input
                        type="text"
                        placeholder="NÃºmero de tarjeta"
                        maxLength="16"
                        onChange={e => setTarjeta({...tarjeta, numero_tarjeta: e.target.value})}
                    />

                    <input
                        type="month"
                        onChange={e => setTarjeta({...tarjeta, fecha_expiracion: e.target.value})}
                    />

                    <input
                        type="text"
                        placeholder="CVV"
                        maxLength="3"
                        onChange={e => setTarjeta({...tarjeta, cvv: e.target.value})}
                    />

                    <button className="checkout-btn" onClick={procesarPago}>
                        Proceder al Pago
                    </button>
                </aside>
            </section>
        </article>
    );
}
