import { obtenerProductos } from "../api/productos";
import { useEffect, useState, useContext } from "react";
import { Link } from "react-router-dom";
import { CarritoContext } from "../context/CarritoContext";

export default function Productos({ filtros }) {

  const [productos, setProductos] = useState([]);

  const { agregarAlCarrito } = useContext(CarritoContext);

  useEffect(() => {
    obtenerProductos().then(setProductos);
  }, []);

  // ---------- FILTRAR PRODUCTOS -----------

  const productosFiltrados = productos.filter(p => {
    
    // Filtrar por categoría
    if (filtros.categoria && String(p.id_categoria) !== filtros.categoria) {
      return false;
    }

    // Tipo de prenda (requiere que lo tengas en tu DB)
    if (filtros.tipo_prenda && p.tipo_prenda !== filtros.tipo_prenda) {
      return false;
    }

    // Estilo (necesita estar en tu DB)
    if (filtros.estilo && p.estilo !== filtros.estilo) {
      return false;
    }

    // Color (requiere columna color)
    if (filtros.color && p.color !== filtros.color) {
      return false;
    }

    // Talla (requiere columna talla)
    if (filtros.talla && p.talla !== filtros.talla) {
      return false;
    }

    // Precio
    if (p.precio < filtros.minPrice || p.precio > filtros.maxPrice) {
      return false;
    }

    return true;
  });

  return (
    <article className="art-newArrivals">

      <div className="box-tarjetas-newArrivals">

        {productosFiltrados.map(p => (
          <div key={p.id_producto} className="tarjeta-newArrivals">

            <div className="box-img-newArrivals">
              <Link className="link-newArrivals" to={`/producto/${p.id_producto}`}>
                <img className="img-newArrivals" src={p.imagen} alt={p.nombre}/>
              </Link>
            </div>

            <div className="box-info-newArrivals">
              <h3>{p.nombre}</h3>
              <p>{p.descripcion}</p>
              <span>Q{p.precio}</span>

              <button 
                className="add-produ" 
                onClick={() => agregarAlCarrito(p)}
              >
                Agregar al carrito
              </button>
            </div>

          </div>
        ))}

      </div>
                        <div class="pagination">
                    <a href="#" class="prev"><i class="fas fa-arrow-left"></i> Anterior</a>
                    <a href="#" class="page-num active">1</a>
                    <a href="#" class="page-num">2</a>
                    <a href="#" class="page-num">3</a>
                    <a href="#" class="page-num">4</a>
                    <a href="#" class="page-num">5</a>
                    <a href="#" class="next">Siguiente <i class="fas fa-arrow-right"></i></a>
                </div>
    </article>
  );
}
