export async function enviarPedido(datos) {
  const respuesta = await fetch(`${API_URL_CARRITO}/api_pago.php`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(datos),
  });

  return await respuesta.json();
}
