import { API_URL_CARRITO } from "./config";

export async function enviarPedido(carrito, total, usuarioId = null) {

  const res = await fetch(`${API_URL_CARRITO}/api_pago.php`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      usuario_id: usuarioId,
      productos: carrito,
      total: total
    })
  });

  return res.json();
}
