import "../estilos/Perfil.css";
import { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import { PedidoContext } from "../context/PedidoContext";

function Perfil() {
  const { usuario, logout } = useContext(AuthContext);
  const { idPedido } = useContext(PedidoContext);

  const [pedidos, setPedidos] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPedidos = async () => {
      try {
        const response = await fetch("http://localhost/INTECAP-BACKEND/TRABAJO_FINAL/historialPedidos.php", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ id_usuario: usuario.id_usuario })
        });

        const data = await response.json();
        setPedidos(data); // tu API devuelve un array de pedidos con detalles
      } catch (error) {
        console.error("Error al obtener pedidos:", error);
      } finally {
        setLoading(false);
      }
    };

    if (usuario?.id_usuario) {
      fetchPedidos();
    }
  }, [usuario]);

  return (
    <main>
      <section>
        <button onClick={logout}>Cerrar Sesión</button>
        <article className="contenedor_perfil">
          <h1>PERFIL DEL USUARIO</h1>
          <p>Id usuario: {usuario?.id_usuario}</p>
          <p>Email: {usuario?.correo}</p>
          <p>Nombre: {usuario?.nombre}</p>
          <p>Cuenta: {usuario?.rol}</p>
        </article>

        <article className="historial_pedidos">
          <h2>Historial de pedidos</h2>
          {loading ? (
            <p>Cargando pedidos...</p>
          ) : pedidos.length === 0 ? (
            <p>No tienes pedidos registrados.</p>
          ) : (
            <div className="tabla-pedidos">
              {pedidos.map((pedido) => (
                <div key={pedido.id_pedido} className="pedido-card">
                  <h3>Pedido #{pedido.id_pedido}</h3>
                  <p>Fecha: {pedido.fecha_pedido}</p>
                  <p>Total: Q{pedido.total}</p>
                  <p>Estado: {pedido.estado}</p>

                  <h4>Detalles:</h4>
                  <ul>
                    {pedido.detalles.map((d) => (
                      <li key={d.id_dpedido}>
                        {d.nombre} x {d.cantidad} — Q{d.subtotal}
                      </li>
                    ))}
                  </ul>
                </div>
              ))}
            </div>
          )}
        </article>
      </section>
    </main>
  );
}

export default Perfil;
