import { useState } from "react";
import { useNavigate } from "react-router-dom";


function Pago() {
  const navigate = useNavigate();

  const [pago, setPago] = useState({
    nombre_tarjeta: "",
    numero_tarjeta: "",
    fecha_expiracion: "",
    cvv: ""
  });

  const handleChange = e => {
    setPago({
      ...pago,
      [e.target.name]: e.target.value
    });
  };

  const handleFinalizarPago = async () => {
    try {
      const response = await fetch("http://localhost/INTECAP-BACKEND/TRABAJO_FINAL/api_pagos.php", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(pago)
      });

      const data = await response.json();
      console.log("Respuesta del backend:", data);

      if (data.mensaje === "pago realizado correctamente") {
        navigate("/confirmacion");  // ← Redirigir al finalizar
      } else {
        alert("Error en el pago: " + data.mensaje);
      }

    } catch (error) {
      console.error("Error al enviar el pago:", error);
      alert("Error en el servidor");
    }
  };

  return (
  		<div>
	      <h2>Información de Pago</h2>
	      <input
	        type="text"
	        name="nombre_tarjeta"
	        placeholder="Nombre en la tarjeta"
	        onChange={handleChange}
	      />

	      <input
	        type="text"
	        name="numero_tarjeta"
	        placeholder="Número de tarjeta"
	        onChange={handleChange}
	      />

	      <input
	        type="month"
	        name="fecha_expiracion"
	        onChange={handleChange}
	      />

	      <input
	        type="text"
	        name="cvv"
	        placeholder="CVV"
	        onChange={handleChange}
	      />

	      <button onClick={handleFinalizarPago}>
	        Finalizar Pago
	      </button>
	    </div> 
  );
}

export default Pago;
