import { productos } from "../assets/imagenes/apis/productos" 
import { obtenerProductos } from "../api/productos"
import { useEffect } from "react"

export default function NewArrivals() {

  useEffect(()=> {
    obtenerProductos().then(setProductos);
  }, []);
  
  return (
    <article className="art-newArrivals">
      <h2>Nuevo Ingreso</h2>
      <div>
        <div className="box-tarjetas-newArrivals">
          {productos.slice(0, 4).map((item, index)=>(
          <div key={index} className="tarjeta-newArrivals">
            <div className="box-img-newArrivals">
              <img className="img-newArrivals" src={item.imagen} alt={item.nombre}/>
            </div>
            <div className="box-info-newArrivals">
              <h3>{item.nombre}</h3>
              <p>{item.descripcion}</p>
              <span>Q{item.precio.toFixed(2)}</span>
            </div>
          </div>

          ))}
        </div>
      </div>
      <div className="box-btn-newArrivals">
        <button className="btn-newArrivals">Ver Todo</button>
      </div>
    </article>
  )
}
