import '../estilos/carrito-productos.css';
import { useContext, useEffect, useState } from 'react';
import { CarritoContext } from '../context/CarritoContext';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faPlus, faMinus } from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';

export default function Carrito() {



    const { carrito, eliminarDelCarrito, reducirCantidad, aumentarCantidad } = useContext(CarritoContext);

    // üßÆ Calcular subtotal
    const subtotal = carrito.reduce((acc, p) => acc + p.precio * p.cantidad, 0);

    // üßÆ Calcular descuento (20% como lo pusiste)
    const descuento = subtotal * 0.20;

    // üßÆ Env√≠o: gratis si subtotal >= 1000
    const envio = subtotal >= 1000 ? 0 : 30;

    // üßÆ Total final
    const total = subtotal - descuento + envio;

    return (
        <article className='cart-page'>
            
            <div className="breadcrumb">
                Inicio / Carrito
            </div>

            <h1>Mi Carrito de Compras</h1>

            <section className="cart-content">
                
                {/* LISTA DE PRODUCTOS */}
                <Link className="link-newArrivals" to={`/producto/${p.id_producto}`}>
                <div className='contenedor-pedidos'>
                    
                    {carrito.length === 0 && (
                        <p>No hay productos en tu carrito.</p>
                    )}

                    {carrito.map(p => (

                        <div key={p.id} className="tarjeta-carrito">


                            <div className="box-img-carrito">
                                <img className="img-carrito" src={p.imagen} alt={p.nombre} />
                            </div>

                            <div className="box-info-carrito">
                                <h3>{p.nombre}</h3>
                                <p>{p.descripcion}</p>
                                <span>Q{p.precio}</span>
                            </div>

                                {/* Controles de cantidad */}

                            <div className='box-controles'>
                                
                                <button className="btn-eliminar" onClick={() => eliminarDelCarrito(p.id_producto)}>
                                    <FontAwesomeIcon icon={faTrash}></FontAwesomeIcon>
                                </button>
                                <div className="cantidad-controles">
                                    <button className='control' onClick={() => reducirCantidad(p.id_producto)}><FontAwesomeIcon icon={faMinus}></FontAwesomeIcon></button>
                                    <span>{p.cantidad}</span>
                                    <button className='control' onClick={() => aumentarCantidad(p.id_producto)}><FontAwesomeIcon icon={faPlus}></FontAwesomeIcon></button>
                                </div>
                            </div>
                        </div>
                    ))}

                </div>
                        </Link>

                {/* RESUMEN */}
                <aside className="cart-summary">
                    <h2>Resumen del Pedido</h2>

                    <div className="summary-line">
                        <span>Subtotal</span>
                        <span>Q{subtotal.toFixed(2)}</span>
                    </div>

                    <div className="summary-line">
                        <span>Descuento (-20%)</span>
                        <span>-Q{descuento.toFixed(2)}</span>
                    </div>

                    <div className="summary-line">
                        <span>Costo de Env√≠o</span>
                        <span>Q{envio.toFixed(2)}</span>
                    </div>

                    <div className="summary-line total-line">
                        <span>Total</span>
                        <span className="total-amount">Q{total.toFixed(2)}</span>
                    </div>

                    <div className="promo-code">
                        <input type="text" placeholder="Ingresar c√≥digo de cup√≥n" />
                        <button>Aplicar</button>
                    </div>

                    <button className="checkout-btn">Proceder al Pago</button>
                </aside>
            </section>

            <div className="cart-info">
                <p>üõçÔ∏è Devoluci√≥n gratuita en 30 d√≠as</p>
                <p>üöö Env√≠o gratis en pedidos mayores a Q1000</p>
            </div>

        </article>
    );
}
