import { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { obtenerHistorial } from "../api/historial_api";
import "../estilos/Historial.css";

export default function HistorialPedidos() {
  const { usuario } = useContext(AuthContext);
  const [historial, setHistorial] = useState([]);
  const [cargando, setCargando] = useState(true);

  useEffect(() => {
    if (!usuario) return;

    obtenerHistorial(usuario.id_usuario)
      .then(setHistorial)
      .finally(() => setCargando(false));
  }, [usuario]);

  if (cargando) return <p className="cargando">Cargando historial...</p>;

  if (historial.length === 0)
    return <p className="sin-pedidos">No tienes pedidos realizados a√∫n.</p>;

  return (
    <div className="historial-container">
      <h2>Historial de Pedidos</h2>

      {historial.map((pedido) => (
        <div className="pedido-card" key={pedido.id_pedido}>
          <h3>Pedido #{pedido.id_pedido}</h3>

          <div className="pedido-items">
            {pedido.items.map((item) => (
              <div className="pedido-item" key={item.id_producto}>
                <img src={item.imagen} alt={item.nombre_producto} />

                <div>
                  <h4>{item.nombre_producto}</h4>
                  <p>Cantidad: {item.cantidad}</p>
                  <p>Subtotal: Q{item.subtotal}</p>
                </div>
              </div>
            ))}
          </div>

          <div className="pedido-total">
            <strong>Total Pagado: Q{pedido.total_pago}</strong>
          </div>
        </div>
      ))}
    </div>
  );
}
