import { productos } from "../assets/imagenes/apis/data"
import {Swiper, SwiperSlide } from "swiper/modules";
import { Navigation, Pagination } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";


export default function NewArrivals() {
  return (
<div className="p-6 bg-gray-100">
      <h2 className="text-2xl font-bold mb-4">Ofertas en Tablets</h2>

      <Swiper
        modules={[Navigation, Pagination]}
        spaceBetween={20}
        slidesPerView={4}
        navigation
        pagination={{ clickable: true }}
        breakpoints={{
          320: { slidesPerView: 1 },
          640: { slidesPerView: 2 },
          1024: { slidesPerView: 4 },
        }}
      >
        {productos.map((product) => (
          <SwiperSlide key={product.id}>
            <div className="bg-white rounded-2xl shadow-md overflow-hidden hover:shadow-lg transition p-3">
              <img
                src={product.image}
                alt={product.name}
                className="w-full h-40 object-cover rounded-md"
              />
              <h3 className="text-lg font-semibold mt-2">{product.name}</h3>
              <p className="text-red-600 font-bold">{product.price}</p>
              {product.discount && (
                <span className="text-sm text-green-600">{product.discount}</span>
              )}
              <button className="mt-3 w-full bg-red-600 text-white rounded-lg py-2 hover:bg-red-700">
                Agregar al carrito
              </button>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
};



