import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCartShopping, faCircleUser } from "@fortawesome/free-solid-svg-icons";
import { Link } from "react-router-dom";
import { CarritoContext } from "../context/CarritoContext";
import { BusquedaContext } from "../context/BusquedaContext";
import { useContext } from "react";

export default function Header() {
  const { carrito } = useContext(CarritoContext);
  const { textoBusqueda, setTextoBusqueda } = useContext(BusquedaContext);

  const totalItems = carrito.reduce((acc, p) => acc + p.cantidad, 0);

  return (
    <header>
      <div className="box-promocion">
        <p>
          Registrate y obten un 20% en tu primer compra. <a href="#">Registrate ahora</a>
        </p>
      </div>

      <nav>
        <Link to={"/"}>
          <img className="logo" src="logo.png" />
        </Link>

        <ul>
          <li>
            <Link to={"/"}>
              <a>Inicio</a>
            </Link>
          </li>
          <li>
            <Link to={"/categoria"}>
              <a>Productos</a>
            </Link>
          </li>
        </ul>

        {/* CUADRO DE BÃšSQUEDA FUNCIONAL */}
        <input
          className="search"
          type="search"
          placeholder="Buscar productos..."
          value={textoBusqueda}
          onChange={(e) => setTextoBusqueda(e.target.value)}
        />

        <div className="box-icons">
          <Link to={"/carrito"} className="box-contador">
            <i>
              <FontAwesomeIcon icon={faCartShopping} />
            </i>

            {totalItems > 0 && (
              <span className="contador-carrito">{totalItems}</span>
            )}
          </Link>

          <a>
            <FontAwesomeIcon icon={faCircleUser} />
          </a>
        </div>
      </nav>
    </header>
  );
}
