import '../estilos/carrito-productos.css';

let cartItems = JSON.parse(localStorage.getItem('cart')) || [];

const SHIPPING_COST = 50.00; // Costo de envío
const FREE_SHIPPING_THRESHOLD = 1000.00; // Envío gratis a partir de Q1000

// Función para formatear a Quetzales
function formatQuetzal(amount) {
    return `Q${amount.toFixed(2)}`;
}

// Función para guardar el estado del carrito en el almacenamiento local y actualizar contador
function saveCart() {
    localStorage.setItem('cart', JSON.stringify(cartItems));
    // Asumiendo que updateCartCount fue definida en script.js o globalmente.
    if (typeof updateCartCount === 'function') {
        updateCartCount(); 
    }
}

// Función para calcular y actualizar el resumen del carrito
function updateCartSummary() {
    let subtotal = 0;
    cartItems.forEach(item => {
        subtotal += item.price * item.quantity;
    });

    // Simulación de descuento del 20% (ajustar según tu lógica real de cupones)
    const promoApplied = false; // Puedes cambiar esto según la lógica del cupón
    const discountRate = promoApplied ? 0.20 : 0;
    const discountAmount = subtotal * discountRate;
    
    const shipping = subtotal >= FREE_SHIPPING_THRESHOLD ? 0.00 : SHIPPING_COST;
    const total = subtotal - discountAmount + shipping;

    // Actualizar el DOM
    document.getElementById('subtotal').textContent = formatQuetzal(subtotal);
    document.getElementById('discount').textContent = formatQuetzal(discountAmount);
    document.getElementById('shipping').textContent = formatQuetzal(shipping);
    document.getElementById('total-amount').textContent = formatQuetzal(total);
    
    // Si el carrito está vacío, mostrar mensaje
    const emptyMessage = document.getElementById('empty-cart-message');
    if (emptyMessage) {
         if (cartItems.length === 0) {
            emptyMessage.style.display = 'block';
         } else {
            emptyMessage.style.display = 'none';
         }
    }
}

// Función para renderizar los elementos del carrito
function renderCartItems() {
    const container = document.getElementById('cart-items-container');
    if (!container) return; 
    
    container.innerHTML = ''; 

    if (cartItems.length === 0) {
        // Mostrar un mensaje si el carrito está vacío
        container.innerHTML = `
            <div id="empty-cart-message" style="text-align: center; padding: 50px; color: #666;">
                <h2>Tu carrito está vacío.</h2>
                <p>¡Añade algunos productos desde la <a href="category.html" style="color: #000; font-weight: bold;">página de categoría</a>!</p>
            </div>
        `;
    } else {
        // Renderizar los artículos si hay
        cartItems.forEach(item => {
            const itemElement = document.createElement('div');
            itemElement.classList.add('cart-item');
            
            const itemSubtotal = item.price * item.quantity;

            itemElement.innerHTML = `
                <img src="https://via.placeholder.com/100x120/E0E0E0/000000?text=${item.image.replace(/ /g, '+')}" alt="${item.name}" class="item-image">
                <div class="item-details">
                    <h3>${item.name}</h3>
                    <p>Color: ${item.color} - Talla: ${item.size}</p>
                    <p class="price">${formatQuetzal(item.price)}</p>
                </div>
                <div class="item-quantity">
                    <button onclick="changeQuantity(${item.id}, -1)">-</button>
                    <span id="quantity-${item.id}">${item.quantity}</span>
                    <button onclick="changeQuantity(${item.id}, 1)">+</button>
                </div>
                <p class="price item-subtotal">${formatQuetzal(itemSubtotal)}</p>
                <i class="fas fa-trash-alt remove-item" onclick="removeItem(${item.id})"></i>
            `;
            container.appendChild(itemElement);
        });
    }

    updateCartSummary();
}

// Funciones de interacción del carrito (ACTUALIZADAS PARA USAR saveCart)
window.changeQuantity = function(itemId, change) {
    const item = cartItems.find(i => i.id === itemId);
    if (item) {
        item.quantity += change;
        if (item.quantity < 1) {
            removeItem(itemId);
        } else {
            saveCart(); 
            renderCartItems();
        }
    }
};

window.removeItem = function(itemId) {
    cartItems = cartItems.filter(i => i.id !== itemId);
    saveCart(); 
    renderCartItems();
};

// Cargar artículos del carrito al cargar la página
document.addEventListener('DOMContentLoaded', () => {
    if (document.querySelector('.cart-page')) {
        renderCartItems();
    }
});
export default function Carrito() {
  return (
    <article className='art-carrito'>
        <div class="breadcrumb">
            Inicio Carrito
        </div>

        <h1>Mi Carrito de Compras</h1>

        <section class="cart-content">
            <div class="cart-items" id="cart-items-container">
                </div>

            <aside class="cart-summary">
                <h2>Resumen del Pedido</h2>
                
                <div class="summary-line">
                    <span>Subtotal</span>
                    <span id="subtotal">Q0.00</span>
                </div>
                <div class="summary-line">
                    <span>Descuento (-20%)</span>
                    <span id="discount">-Q0.00</span>
                </div>
                <div class="summary-line">
                    <span>Costo de Envío</span>
                    <span id="shipping">Q0.00</span>
                </div>
                
                <div class="summary-line total-line">
                    <span>Total</span>
                    <span id="total-amount" class="total-amount">Q0.00</span>
                </div>
                
                <div class="promo-code">
                    <input type="text" id="promo-input" placeholder="Ingresar código de cupón"/>
                    <button id="apply-promo">Aplicar</button>
                </div>
                
                <button class="checkout-btn">Proceder al Pago</button>
            </aside>
        </section>
        
        <div class="cart-info">
            <p><i class="fas fa-undo-alt"></i> Devolución gratuita en 30 días</p>
            <p><i class="fas fa-truck"></i> Envío gratuito en pedidos superiores a Q1000</p>
        </div>

    </article>
  )
}
