import { useState, useContext, useEffect } from "react";
import Productos from "./Productos";
import { BusquedaContext } from "../context/BusquedaContext";

import "./categoria.css"; // tu estilo

function Categoria() {
  const [productos, setProductos] = useState([]);
  const [busqueda, setBusqueda] = useState("");

  const obtenerProductos = async () => {
    try {
      const respuesta = await fetch("http://localhost/Ejemplos.PHP/crud_sql/productos/api_productos.php");
      const data = await respuesta.json();

      // Validar que realmente existan productos en la respuesta
      if (Array.isArray(data)) {
        setProductos(data);
      } else {
        console.error("La API NO devolvi칩 un array v치lido:", data);
        setProductos([]);
      }
    } catch (error) {
      console.error("Error al obtener productos:", error);
    }
  };

  useEffect(() => {
    obtenerProductos();
  }, []);

  const filtrarProductos = productos.filter((prod) => {
    const nombre = prod.nombre ? prod.nombre.toLowerCase() : "";
    return nombre.includes(busqueda.toLowerCase());
  });

  return (
    <div className="categoria-contenedor">

      {/* 游댍 BARRA DE B칔SQUEDA */}
      <div className="barra-busqueda">
        <input
          type="text"
          placeholder="Buscar producto..."
          value={busqueda}
          onChange={(e) => setBusqueda(e.target.value)}
        />
      </div>

      {/* 游 LISTA DE PRODUCTOS FILTRADOS */}
      <div className="lista-productos">
        {filtrarProductos.length > 0 ? (
          filtrarProductos.map((prod) => (
            <div className="item-producto" key={prod.id}>
              <h3>{prod.nombre}</h3>
              <p>Precio: Q{prod.precio}</p>
              <p>Stock: {prod.stock}</p>
            </div>
          ))
        ) : (
          <p>No hay productos que coincidan con " {busqueda} "</p>
        )}
      </div>
    </div>
  );
}

export default Categoria;
