import { API_URL_CARRITO } from "./config";

export async function enviarPedido(datos) {
  const respuesta = await fetch(`${API_URL_CARRITO}`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(datos),
  });

  return await respuesta.json();
}
