import { useState } from "react";
import "../estilos/login.css";
import { useNavigate, Link } from "react-router-dom";

export const Registro = () => {
  const [form, setForm] = useState({
    nombre: "",
    apellido: "",
    correo: "",
    telefono: "",
    pass: ""
  });

  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    try {
      const response = await fetch("http://localhost/Ejemplos.PHP/crud_sql/productos/registro.php", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      });

      const data = await response.json();

      if (data.error) {
        setError(data.error);
      } else {
        setSuccess("Registro exitoso, puedes iniciar sesión ✔");
        setTimeout(() => navigate("/Login"), 2000); // volver al login
      }

    } catch (err) {
      setError("Error al conectar con el servidor.");
    }
  };

  return (
    <div className="login-container">
      <h2>Crear Cuenta</h2>

      <form onSubmit={handleSubmit} className="login-form">

        <input name="nombre" placeholder="Nombre" onChange={handleChange} />
        <input name="apellido" placeholder="Apellido" onChange={handleChange} />
        <input name="correo" type="email" placeholder="Correo" onChange={handleChange} />
        <input name="telefono" placeholder="Teléfono" onChange={handleChange} />
        <input name="pass" type="password" placeholder="Contraseña" onChange={handleChange} />

        <button type="submit" className="login-btn">Registrarme</button>

        <Link to="/Login" className="login-btn">
          Ya tengo cuenta
        </Link>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}
      {success && <p style={{ color: "green" }}>{success}</p>}
    </div>
  );
};
