import { obtenerProductos } from "../api/productos"
import { useEffect, useState, useContext} from "react"
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCartArrowDown } from "@fortawesome/free-solid-svg-icons";
import { CarritoContext } from "../context/CarritoContext";
import { useParams } from "react-router-dom"

export default function Prodcutos() {

  const {agregarAlCarrito} = useContext(CarritoContext);

    function opcionAgregar(){
      agregarAlCarrito(producto);
    }

  const {id} = useParams();
  const [producto, setProducto] = useState(null);
  
  useEffect(() =>{
      obtenerProductosId(id).then(setProducto); 
    }, [id]);

  if (!productos) return <p>Cargando...</p>;
  
  const [productos, setProductos] = useState([]);
  useEffect(()=> {
    obtenerProductos().then(setProductos);
  }, []);

  return (
    <article className="art-newArrivals">
      <div>
        <div className="box-tarjetas-newArrivals">
          {productos.map((p)=>(
            <Link className="link-newArrivals" to={`/producto/${p.id_producto}`}>
              <div key={p.id} className="tarjeta-newArrivals">
                <div className="box-img-newArrivals">
                  <i className="add-produ" onClick={opcionAgregar}><FontAwesomeIcon icon={faCartArrowDown}></FontAwesomeIcon></i>
                  <img className="img-newArrivals" src={p.imagen} alt={p.nombre}/>
                </div>
                <div className="box-info-newArrivals">
                  <h3>{p.nombre}</h3>
                  <p>{p.descripcion}</p>
                  <span>Q{p.precio}</span>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </div>
    </article>
  )
}
