import { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import "../estilos/Pago.css";
import { AuthContext } from "../context/AuthContext";


function Pago() {

  const {usuario} = useContext(AuthContext); 

  const carrito = JSON.parse(localStorage.getItem("carrito")) || [];

  const id_pedido = 1;  // de ejemplo

  const navigate = useNavigate();

  const [pago, setPago] = useState({
    nombre_tarjeta: "",
    numero_tarjeta: "",
    fecha_expiracion: "",
    cvv: ""
  });

  const handleChange = e => {
    setPago({
      ...pago,
      [e.target.name]: e.target.value
    });
  };

  const handleFinalizarPago = async () => {

    // JSON ENVIADO
    const datosEnviar = {
      id_pedido: Number(id_pedido),
      id_usuario: Number(usuario.id_usuario),
      carrito: carrito,
      pago: pago
    };

    console.log("JSON a enviar:", datosEnviar);

    try {
      const response = await fetch(
        "http://localhost/INTECAP-BACKEND/TRABAJO_FINAL/api_pago.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(datosEnviar)
        }
      );

      const data = await response.json();
	      console.log("Respuesta del backend:", data);

	      if (data.mensaje === "pago realizado correctamente") {
	        navigate("/confirmacion");
	      } else {
	        alert("Error en el pago: " + data.mensaje);
      }

    } catch (error) {
      console.error("Error:", error);
      alert("Error en el servidor");
    }
  };


  return (
  		 <div className="pago-contenedor">
		    <h2>
		      <i className="fas fa-credit-card"></i>
		      Información de Pago
		    </h2>

		    {/* Nombre en la tarjeta */}
		    <div className="pago-grupo">
		      <i className="fas fa-user"></i>
		      <input
		        type="text"
		        name="nombre_tarjeta"
		        placeholder="Nombre en la tarjeta"
		        className="pago-input"
		        onChange={handleChange}
		      />
		    </div>

		    {/* Número de tarjeta */}
		    <div className="pago-grupo">
		      <i className="fas fa-credit-card"></i>
		      <input
		        type="text"
		        name="numero_tarjeta"
		        placeholder="Número de tarjeta"
		        className="pago-input"
		        onChange={handleChange}
		      />
		    </div>

		    {/* Fecha de expiración */}
		    <div className="pago-grupo">
		      <i className="fas fa-calendar-alt"></i>
		      <input
		        type="text"
		        name="fecha_expiracion"
		        className="pago-input"
		        placeholder="AÑO - MES"
		        onChange={handleChange}
		      />
		    </div>

		    {/* CVV */}
		    <div className="pago-grupo">
		      <i className="fas fa-lock"></i>
		      <input
		        type="text"
		        name="cvv"
		        placeholder="CVV"
		        className="pago-input"
		        onChange={handleChange}
		      />
		    </div>

		    <button className="btn-pagar" onClick={handleFinalizarPago}>
		      Finalizar Pago &nbsp; <i className="fas fa-arrow-right"></i>
		    </button>

  		</div>
  );
}

export default Pago;
